withErrorHandling.code:
import { copyToClipboard } from "@react-ts-ui-lib/utilities";

const handleCopy = async () => {
  try {
    const success = await copyToClipboard("Text k kopírování");
    if (success) {
      // Úspěch
      alert("Zkopírováno!");
    } else {
      // Neúspěch
      alert("Kopírování selhalo.");
    }
  } catch (error) {
    console.error("Chyba při kopírování:", error);
  }
};

